    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        // Handle action bar item clicks here. The action bar will
        // automatically handle clicks on the Home/Up button, so long
        // as you specify a parent activity in AndroidManifest.xml.
        int id = item.getItemId();

        //noinspection SimplifiableIfStatement
        if (id == R.id.action_incluir) {
            //Intent para acessar a proxima tela (activity)
            intent = new Intent(this, IncluirNovoActivity.class);

        }

        if (id == R.id.action_editar) {
            intent = new Intent(this, EditarActivity.class);
           // return true;
        }

        startActivity(intent);

        return super.onOptionsItemSelected(item);
    }

    @Override
    protected void onActivityResult(int requestCode, int resultCode, Intent data) {
        super.onActivityResult(requestCode, resultCode, data);
        switch (requestCode) {
            case IntentIntegrator.REQUEST_CODE:

                IntentResult scanResult = IntentIntegrator.parseActivityResult(requestCode,
                        resultCode, data);
                if (scanResult == null) {
                    return;
                }

                //Obtem o codigo de barras
                String result = scanResult.getContents();
                if (result != null) {

                    Produto produto = Query.one(Produto.class, "select * from produto where codigo_barras = ?", result).get();

                    if (produto!=null){

                        Item item = new Item();
                        item.setId(0L);
                        item.setQuantidade(1);
                        item.setIdProduto(produto.getId());
                        item.setIdCompra(carrinho.getIdCompra()); //carrinho é global por isso consigo o get
                        item.save();

                        ItemProduto itemProduto = new ItemProduto();
                        itemProduto.setDescricao(produto.getDescricao());
                        itemProduto.setQuantidade(item.getQuantidade());
                        itemProduto.setFoto(produto.getFoto());

                        list.add(itemProduto);
                        adapter.notifyDataSetChanged();


                    }else{

                        //Toast.makeText(this, result, Toast.LENGTH_LONG).show();
                        Toast.makeText(this, "Produto não localizado", Toast.LENGTH_LONG).show();

                    }


                }
                break;
            default:
        }

    }


